function tracePacket(prefix, bytes) {
	return;
	for (let i = 0; i < bytes.length; i += 16) {
		let line = prefix;
		let end = i + 16;
		if (end > bytes.length) end = bytes.length;
		for (let j = i; j < end; j++) {
			let byte = bytes[j].toString(16);
			if (byte.length < 2) byte = "0" + byte;
			line += byte + " ";
		}
		line += "   ";
		for (let j = i; j < end; j++) {
			let byte = bytes[j];
			if ((32 <= byte) && (byte < 128))
				line += String.fromCharCode(byte);
			else
				line += ".";
		}
		console.log(line);
	}
}